$(document).ready(function(){
    $(".owl-carousel").owlCarousel({
        items: 3, // Jumlah item yang ditampilkan per slide
        loop: true, // Mengaktifkan mode loop
        margin: 20, // Jarak antara item
        responsive:{
            0:{
                items:1
            },
            600:{
                items:3
            }
        }
    });
});